const CTA = ({ handleClick, isEmailValid, setIsEmailValid, ctaRef }) => {
const [emailId, setEmailId] = useState("");

  const submitHandler = () => {
    handleClick(emailId);
  };

  return (
    <div ref={ctaRef} className="CTA container-fluid">
      <div className="row">
        <div className="CTA-content col-12 col-md-12 col-lg-6 col-xl-6 ">
          <h2 className="container-title">Subscribe to our Newsletter</h2>
          <p className="container-text CTA-caption">
          Get exciting Finance news and updates
          </p>
        </div>
        <div className="col-12 col-md-12 col-lg-6 col-xl-6">
          <div className="CTA-form">
            <Link
              text="Please enter your email"
              emailId={emailId}
              setEmailId={setEmailId}
              isEmailValid={isEmailValid}
              setIsEmailValid={setIsEmailValid}
            />
            <Button type="btn--primary" text="Subscribe" submitHandler={submitHandler} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Footer;
